import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function App() {
  const [priceData, setPriceData] = useState(null);  // For storing price data
  const [error, setError] = useState('');

  // Fetch Bitcoin price data for the past 24 hours from CoinGecko
  const fetchPriceData = async () => {
    try {
      const response = await fetch('https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=usd&days=1&interval=hourly');
      if (!response.ok) throw new Error('Failed to fetch price data');
      const data = await response.json();

      // Extract timestamps and prices from the response
      const labels = data.prices.map(pricePoint => {
        const date = new Date(pricePoint[0]);  // Convert timestamp to Date
        return date.getHours() + ':00';        // Extract hour (e.g., "14:00")
      });

      const prices = data.prices.map(pricePoint => pricePoint[1]);  // Extract prices

      setPriceData({
        labels: labels,          // X-axis labels (hours)
        datasets: [
          {
            label: 'Bitcoin Price (USD)',
            data: prices,        // Y-axis data (prices)
            borderColor: 'rgba(75, 192, 192, 1)',  // Line color
            backgroundColor: 'rgba(75, 192, 192, 0.2)',  // Fill color
            fill: true,          // Enable area fill under the line
          },
        ],
      });
    } catch (err) {
      setError(err.message);
    }
  };

  // Fetch data when the component mounts
  useEffect(() => {
    fetchPriceData();
  }, []);

  return (
    <div className="App">
      <h1>Bitcoin Price - Last 24 Hours</h1>

      {/* Render the chart */}
      {priceData ? (
        <Line
          data={priceData}
          options={{
            responsive: true,
            plugins: {
              legend: { display: true, position: 'top' },
              title: { display: true, text: 'Bitcoin Price in USD (Last 24 Hours)' },
            },
            scales: {
              x: { title: { display: true, text: 'Time (Hourly)' } },
              y: { title: { display: true, text: 'Price (USD)' } },
            },
          }}
        />
      ) : (
        <p>{error ? error : 'Loading price data...'}</p>
      )}
    </div>
  );
}

export default App;
