import React, { useState, useEffect } from 'react';

function App() {
  // State variables to hold data
  const [blockHeight, setBlockHeight] = useState(null);  // On-chain: Bitcoin block height
  const [btcPrice, setBtcPrice] = useState(null);        // Off-chain: Bitcoin price in USD
  const [btcMetrics, setBtcMetrics] = useState(null);    // Other off-chain metrics
  const [onChainMetrics, setOnChainMetrics] = useState(null); // On-chain metrics
  const [error, setError] = useState('');

  // Fetch Bitcoin block height (on-chain data from Blockchair API)
  const fetchBlockHeight = async () => {
    try {
      const response = await fetch('https://api.blockchair.com/bitcoin/stats');
      if (!response.ok) throw new Error('Failed to fetch block height');
      const data = await response.json();
      setBlockHeight(data.data.blocks);  // Set block height from Blockchair API
    } catch (err) {
      setError(err.message);
    }
  };

  // Fetch Bitcoin price and other off-chain data (from CoinGecko)
  const fetchBtcMetrics = async () => {
    try {
      const response = await fetch('https://api.coingecko.com/api/v3/coins/bitcoin?localization=false');
      if (!response.ok) throw new Error('Failed to fetch Bitcoin metrics');
      const data = await response.json();

      setBtcPrice(data.market_data.current_price.usd);  // Set Bitcoin price in USD
      setBtcMetrics({
        marketCap: data.market_data.market_cap.usd,      // Market capitalization
        volume24h: data.market_data.total_volume.usd,    // 24h volume
        priceChange24h: data.market_data.price_change_percentage_24h,  // 24h price change
        priceChange7d: data.market_data.price_change_percentage_7d,    // 7d price change
      });
    } catch (err) {
      setError(err.message);
    }
  };

  // Fetch on-chain metrics (from Glassnode API)
  const fetchOnChainMetrics = async () => {
    try {
      const response = await fetch('https://api.glassnode.com/v1/metrics/addresses/active_count?a=BTC', {
        headers: {
          'Authorization': `Bearer YOUR_API_KEY`,  // Replace with your Glassnode API key
        },
      });
      if (!response.ok) throw new Error('Failed to fetch on-chain metrics');
      const activeAddresses = await response.json();

      const transactionVolumeRes = await fetch('https://api.glassnode.com/v1/metrics/transactions/transfers_volume_sum?a=BTC', {
        headers: {
          'Authorization': `Bearer YOUR_API_KEY`,  // Replace with your Glassnode API key
        },
      });
      const transactionVolume = await transactionVolumeRes.json();

      const minerRevenueRes = await fetch('https://api.glassnode.com/v1/metrics/mining/revenue_sum?a=BTC', {
        headers: {
          'Authorization': `Bearer YOUR_API_KEY`,  // Replace with your Glassnode API key
        },
      });
      const minerRevenue = await minerRevenueRes.json();

      setOnChainMetrics({
        activeAddresses: activeAddresses[0].value,   // Active Bitcoin addresses
        transactionVolume: transactionVolume[0].value,  // Total transaction volume
        minerRevenue: minerRevenue[0].value,  // Miner revenue
      });
    } catch (err) {
      setError(err.message);
    }
  };

  // Use `useEffect` to fetch data on component mount
  useEffect(() => {
    fetchBlockHeight();
    fetchBtcMetrics();
    fetchOnChainMetrics();
  }, []);

  return (
    <div className="App">
      <h1>Bitcoin Dashboard</h1>

      {/* Display block height (on-chain) */}
      {blockHeight !== null ? (
        <p>Current Bitcoin Block Height: {blockHeight}</p>
      ) : (
        <p>Loading block height...</p>
      )}

      {/* Display Bitcoin price and other off-chain data */}
      {btcPrice !== null ? (
        <div>
          <p>Bitcoin Price (USD): ${btcPrice}</p>
          <p>Market Cap: ${btcMetrics.marketCap?.toLocaleString()}</p>
          <p>24h Volume: ${btcMetrics.volume24h?.toLocaleString()}</p>
          <p>24h Price Change: {btcMetrics.priceChange24h}%</p>
          <p>7d Price Change: {btcMetrics.priceChange7d}%</p>
        </div>
      ) : (
        <p>Loading Bitcoin metrics...</p>
      )}

      {/* Display on-chain metrics */}
      {onChainMetrics !== null ? (
        <div>
          <h2>On-Chain Metrics</h2>
          <p>Active Bitcoin Addresses: {onChainMetrics.activeAddresses}</p>
          <p>On-Chain Transaction Volume: {onChainMetrics.transactionVolume.toLocaleString()} BTC</p>
          <p>Miner Revenue: ${onChainMetrics.minerRevenue.toLocaleString()}</p>
        </div>
      ) : (
        <p>Loading on-chain metrics...</p>
      )}

      {/* Display any errors */}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default App;
